// Given a string s, find the longest palindromic substring in s. You may assume that the maximum length of s is 1000.

// Example 1:
// Input: "babad"
// Output: "bab"
// Note: "aba" is also a valid answer.

// Example 2:
// Input: "cbbd"
// Output: "bb"

describe("Given a string s, find the longest palindromic substring in s. You may assume that the maximum length of s is 1000.", function () {
  const { getLongestPalindromicSubstring } = require("../src/app");

  it("should return relsult a when given input is a.", function () {
    expect("aa").toEqual(getLongestPalindromicSubstring("aa"));
  });

  it("should return relsult bab when given input is babad.", function () {
    expect("bab").toEqual(getLongestPalindromicSubstring("babad"));
  });

  it("should return relsult bb when given input is cbbd.", function () {
    expect("bb").toEqual(getLongestPalindromicSubstring("cbbd"));
  });

  it("should return relsult aaxaaxaa when given input is xaaxaaxaayaaxeefofeejaaygaxaay.", function () {
    expect("aaxaaxaa").toEqual(
      getLongestPalindromicSubstring("xaaxaaxaayaaxeefofeejaaygaxaay")
    );
  });

  it("should return relsult '' when given input is abcf.", function () {
    expect("a").toEqual(getLongestPalindromicSubstring("abcf"));
  });

  it("should return relsult a when given input is a.", function () {
    expect("a").toEqual(getLongestPalindromicSubstring("a"));
  });

  it("should return relsult aca when given input is aacabdkacaa.", function () {
    expect("aca").toEqual(getLongestPalindromicSubstring("aacabdkacaa"));
  });

  it("should return relsult bcb when given input is eabcb.", function () {
    expect("bcb").toEqual(getLongestPalindromicSubstring("eabcb"));
  });

  it("should return relsult  when given input is .", function () {
    expect(
      ""
    ).toEqual(
      getLongestPalindromicSubstring(
        ""
      )
    );
  });

  it("should return relsult a when given input is abcda.", function () {
    expect("a").toEqual(getLongestPalindromicSubstring("abcda"));
  });

  it("should return relsult adada when given input is babadada.", function () {
    expect("adada").toEqual(getLongestPalindromicSubstring("babadada"));
  });

  it("should return relsult xxx when given input is xxxxx.", function () {
    expect("qosoq").toEqual(
      getLongestPalindromicSubstring(
        ""
      )
    );
  });
});
